import{_ as e,c as r,o as a,R as o}from"./chunks/framework.sjnMwbWx.js";const k=JSON.parse('{"title":"Visual Studio Code 扩展收集","description":"","frontmatter":{"tags":["note","vscode","markdown"]},"headers":[],"relativePath":"tech/other/vscode-extensions.md","filePath":"tech/other/vscode-extensions.md","lastUpdated":1701662868000}'),n={name:"tech/other/vscode-extensions.md"},t=o('<h1 id="visual-studio-code-扩展收集" tabindex="-1">Visual Studio Code 扩展收集 <a class="header-anchor" href="#visual-studio-code-扩展收集" aria-label="Permalink to &quot;Visual Studio Code 扩展收集&quot;">​</a></h1><h2 id="美化" tabindex="-1">美化 <a class="header-anchor" href="#美化" aria-label="Permalink to &quot;美化&quot;">​</a></h2><ol><li><a href="vscode:extension/PKief.material-icon-theme" target="_blank" rel="noreferrer">Material Icon Theme</a> - 美化文件图标。</li><li><a href="vscode:extension/GitHub.github-vscode-theme" target="_blank" rel="noreferrer">Github Theme</a> - Github 风格主题。</li><li><a href="vscode:extension/barrsan.reui" target="_blank" rel="noreferrer">ReUI</a> - React 风格主题。</li></ol><h2 id="markdown" tabindex="-1">Markdown <a class="header-anchor" href="#markdown" aria-label="Permalink to &quot;Markdown&quot;">​</a></h2><ol><li><a href="vscode:extension/yzhang.markdown-all-in-one" target="_blank" rel="noreferrer">Markdown all in one</a> - Markdown 所需的一切（键盘快捷键、目录、自动预览等）。</li><li><a href="vscode:extension/DavidAnson.vscode-markdownlint" target="_blank" rel="noreferrer">markdownlint</a> - MD 语法检查提示。</li><li><a href="vscode:extension/jebbs.plantuml" target="_blank" rel="noreferrer">PlantUML</a> - 增加 PlantUML 支持。</li><li><a href="vscode:extension/bierner.markdown-mermaid" target="_blank" rel="noreferrer">Markdown Preview Mermaid Support</a> - 增加 Mermaid 和流程图支持。</li><li><a href="vscode:extension/xlthu.Pangu-Markdown" target="_blank" rel="noreferrer">Pangu-Markdown</a> - 盘古之白 for Markdown，优化中文排版。</li><li><a href="vscode:extension/bierner.markdown-preview-github-styles" target="_blank" rel="noreferrer">Markdown Preview Github Styling</a> - 将自带的 MD 渲染预览样式更改为 Github 的渲染样式。</li><li><a href="vscode:extension/shd101wyy.markdown-preview-enhanced" target="_blank" rel="noreferrer">Markdown Preview Enhanced</a> - 增加全新的渲染预览视图，图片助手。</li></ol><h2 id="日志" tabindex="-1">日志 <a class="header-anchor" href="#日志" aria-label="Permalink to &quot;日志&quot;">​</a></h2><ol><li><a href="vscode:extension/pajoma.vscode-journal" target="_blank" rel="noreferrer">VSCode Journal</a> - 快速创建日志（日记）。</li><li><a href="vscode:extension/Gruntfuggly.vscode-journal-view" target="_blank" rel="noreferrer">vscode-journal-view</a> - 配合上面的快速创建日志插件使用，为侧边栏增加日志视图，方便浏览。</li></ol><h2 id="画图" tabindex="-1">画图 <a class="header-anchor" href="#画图" aria-label="Permalink to &quot;画图&quot;">​</a></h2><ol><li><a href="vscode:extension/hediet.vscode-drawio" target="_blank" rel="noreferrer">Draw.io Integration</a> - Draw.io 离线版，使用 <code>*.drawio.svg</code> 或 <code>*.drawio.png</code> 后缀名创建文件，即可进行编辑／使用（无需导出）。</li><li><a href="vscode:extension/pomdtr.excalidraw-editor" target="_blank" rel="noreferrer">Excalidraw</a> - Excalidraw 离线版，使用 <code>*.excalidraw.svg</code> 后缀名创建文件，即可进行编辑／使用（无需导出）。</li></ol><h2 id="web-开发" tabindex="-1">Web 开发 <a class="header-anchor" href="#web-开发" aria-label="Permalink to &quot;Web 开发&quot;">​</a></h2><ol><li><a href="vscode:extension/formulahendry.auto-rename-tag" target="_blank" rel="noreferrer">Auto Rename Tag</a> - 帮你自动重命名成对的 HTML/XML 标记，提高效率。</li></ol><h2 id="其他" tabindex="-1">其他 <a class="header-anchor" href="#其他" aria-label="Permalink to &quot;其他&quot;">​</a></h2><ol><li><a href="vscode:extension/grasspy.autopunc" target="_blank" rel="noreferrer">中文标点符号转换</a> - 将用户输入的中文标点符号自动更改为英文标点符号。</li><li><a href="vscode:extension/yzhang.dictionary-completion" target="_blank" rel="noreferrer">Dictionary Completion</a> - 英文单词补全。</li><li><a href="vscode:extension/holmescn.vscode-wordcount-cjk" target="_blank" rel="noreferrer">Word Count CJK</a> - 中文字数统计。</li><li><a href="vscode:extension/cweijan.vscode-office" target="_blank" rel="noreferrer">Office Viewer</a> - 支持预览常见的 office 文件格式。</li><li><a href="vscode:extension/xyz.local-history" target="_blank" rel="noreferrer">Local History</a> - 每次修改文件时，都会在本地保留包含旧内容的历史记录。</li><li><a href="vscode:extension/hancel.google-translate" target="_blank" rel="noreferrer">Google Translate</a> - 在编辑器中提供 Google 翻译，无需秘钥。</li></ol><h2 id="蛇精病" tabindex="-1">蛇精病 <a class="header-anchor" href="#蛇精病" aria-label="Permalink to &quot;蛇精病&quot;">​</a></h2><ol><li><a href="vscode:extension/sakura1357.cxk" target="_blank" rel="noreferrer">坤坤鼓励师</a> - 在 VS Code 中连续写代码一小时（时间可配置），会有蔡徐坤专属篮球舞提醒你该休息啦！</li></ol>',15),l=[t];function i(d,s,c,h,f,u){return a(),r("div",null,l)}const v=e(n,[["render",i]]);export{k as __pageData,v as default};
